mapper.select:
	
		
			select * from ( 
				select /*+INDEX_DESC(book_author 인덱스명) */ rownum as rn, authorid, authorname, nationid, regdate, updatedate 
            	from book_author  
            	where 
		
			
	    	
	    	
	    	
			
				authorname like '%' || #{keyword} || '%' and 
			
		
		        
				rownum <= #{pageNum} * #{amount} 
				) 
			where rn > (#{pageNum} - 1) * #{amount}

mapper.update:

	update book_author 
	set authorName=#{authorName}, nationId=#{nationId}, authorIntro=#{authorIntro}, updatedate=sysdate 
	where authorId = #{authorId}
