mapper.select:

		select * from(  		
			select rownum as rn, replyId, bookId, memberId, content, rating, regDate
			from book_reply
			where rownum <= #{pageNum} * #{amount} and bookId = #{bookId}	
			order by regDate desc
  			)
  		where rn > (#{pageNum} -1) * #{amount}

select count(*)
from book_reply
where bookId = #{bookId}
